import { useState, useEffect } from "react";

function App() {
  const [loading, setLoading] = useState(true);
  //한번만 작동하도록 
  useEffect(() => {
    fetch("https://api.coinpaprika.com/v1/tickers");
  }, [])
  return ( 
  <div>
    <h1>The Coins!</h1>
    {loading ? <strong>Loading...</strong>: null}
  </div>
  );
}

export default App;
