import styled from "styled-components";

const Father = styled.div`
  display: flex;
`;

const Input = styled.input.attrs({required: true})`
  background-color: tomato;
`;



//이전 방법처럼 확장하는 것이 아닌 html 태그를 변경하는 방법 
function App() {
  return <Father as="header">
    <Input />
    <Input />
    <Input />
    <Input />
    <Input />
  </Father>;

}

export default App;
